package assignment1;

public class GradeRange {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=66;
		if(i>=90 && i<=100) {
			System.out.println(i+ " Grade O ");
		}else if(i>=80 && i<=89){
			System.out.println(i+ " Grade A ");
		}else if(i>=70 && i<=79) {
			System.out.println(i+ " Grade B ");
		}else {
			System.out.println("Fail");
		}
		
	}

}
