package assignment1;

public class positiveornegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=14;
		if(i<0) {
			System.out.println("Negative Number");
		}else if(i>0) {
			System.out.println("Positive Number");
		}else {
			System.out.println("Zero");
		}
	}

}
